        .result-correct {
            background-color: var(--vscode-terminal-ansiGreen);
            color: var(--vscode-terminal-background);
        }
        .result-incorrect {
            background-color: var(--vscode-terminal-ansiRed);
            color: var(--vscode-terminal-background);
        }
