            existingEntry.averageScore = existingEntry.totalScore / existingEntry.challengesCompleted;
        }
        else {
            leaderboard.push({
                username,
                totalScore: newScore,
                challengesCompleted: 1,
                lastPlayed: new Date(),
                averageScore: newScore
            });
        }
