        catch (error) {
            // Fallback to asking user
        }
        // Ask user for username if not available
        const username = await vscode.window.showInputBox({
            prompt: 'Enter your username for the leaderboard',
            placeHolder: 'Your username',
            value: 'Anonymous'
        });
